// Analyze hospital capacity and ambulance availability
const data = $json.data;
const hospitals = data.hospitals;
const ambulances = data.ambulances;

// Calculate metrics
const availableHospitals = hospitals.filter(h => h.available).length;
const totalHospitals = hospitals.length;
const availabilityRate = (availableHospitals / totalHospitals) * 100;

const availableAmbulances = ambulances.available;
const totalAmbulances = ambulances.total;
const ambulanceRate = (availableAmbulances / totalAmbulances) * 100;

// Determine alert levels
const alerts = [];

// Hospital capacity alerts
if (availabilityRate < 50) {
  alerts.push({
    type: 'critical',
    category: 'hospital_capacity',
    message: `CRITICAL: Only ${availabilityRate.toFixed(1)}% of hospitals available (${availableHospitals}/${totalHospitals})`,
    priority: 'high'
  });
} else if (availabilityRate < 70) {
  alerts.push({
    type: 'warning',
    category: 'hospital_capacity',
    message: `WARNING: Hospital availability at ${availabilityRate.toFixed(1)}% (${availableHospitals}/${totalHospitals})`,
    priority: 'medium'
  });
}

// Ambulance availability alerts
if (ambulanceRate < 30) {
  alerts.push({
    type: 'critical',
    category: 'ambulance_availability',
    message: `CRITICAL: Only ${availableAmbulances} ambulances available (${ambulanceRate.toFixed(1)}%)`,
    priority: 'high'
  });
} else if (ambulanceRate < 50) {
  alerts.push({
    type: 'warning',
    category: 'ambulance_availability',
    message: `WARNING: Low ambulance availability - ${availableAmbulances} units (${ambulanceRate.toFixed(1)}%)`,
    priority: 'medium'
  });
}

// Check individual hospital issues
const unavailableHospitals = hospitals.filter(h => !h.available);
if (unavailableHospitals.length > 0) {
  unavailableHospitals.forEach(hospital => {
    alerts.push({
      type: 'info',
      category: 'hospital_unavailable',
      message: `${hospital.name} is currently unavailable`,
      hospital_id: hospital.id,
      priority: 'low'
    });
  });
}

return [{
  json: {
    timestamp: new Date().toISOString(),
    metrics: {
      hospital_availability: availabilityRate,
      ambulance_availability: ambulanceRate,
      available_hospitals: availableHospitals,
      total_hospitals: totalHospitals,
      available_ambulances: availableAmbulances,
      total_ambulances: totalAmbulances
    },
    alerts,
    needs_attention: alerts.some(a => a.priority === 'high'),
    system_status: alerts.length === 0 ? 'optimal' : 
                   alerts.some(a => a.type === 'critical') ? 'critical' : 'warning'
  }
}];
